

def test( func ) -> None :

    tests = [
        ( [ 1, 2, 3 ],  [[1, 2, 3]])
    ]

    for idx, t in enumerate( __tests() ):
        
        result   = func( arr = t[0] )
        expected = t[1]


        print( f"### Test { idx + 1 } { 'OK' if result == expected else 'Fail' }" )
        if result != expected:
            print( f"Expected :\n{ t[1] }" )
            print( f"Got :\n{ func( t[0] ) }" )
            print("#"*100)


